import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import ResultDisplay from './components/ResultDisplay';
import { AppState } from './types';
import { ApiService, downloadAsFile } from './services/api';

const App: React.FC = () => {
  const [state, setState] = useState<AppState>({
    isLoading: false,
    error: null,
    uploadedFile: null,
    extractedText: null,
    generatedRequirements: null,
    currentStep: 'upload',
  });

  const handleFileSelect = async (file: File) => {
    setState(prev => ({
      ...prev,
      isLoading: true,
      error: null,
      uploadedFile: file,
      currentStep: 'processing',
    }));

    try {
      const response = await ApiService.uploadAndGenerate(file);

      setState(prev => ({
        ...prev,
        isLoading: false,
        extractedText: response.extracted_text,
        generatedRequirements: response.generated_requirements,
        currentStep: 'result',
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: error instanceof Error ? error.message : '‰∫àÊúü„Åó„Å™„ÅÑ„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü',
        currentStep: 'upload',
      }));
    }
  };

  const handleDownload = () => {
    if (state.generatedRequirements && state.uploadedFile) {
      const filename = `„Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏_${state.uploadedFile.name.split('.')[0]}.md`;
      downloadAsFile(state.generatedRequirements, filename);
    }
  };

  const handleReset = () => {
    setState({
      isLoading: false,
      error: null,
      uploadedFile: null,
      extractedText: null,
      generatedRequirements: null,
      currentStep: 'upload',
    });
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column'
    }}>
      <header style={{
        background: 'linear-gradient(135deg, #007bff 0%, #0056b3 100%)',
        color: 'white',
        padding: '32px 0',
        textAlign: 'center'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px'
        }}>
          <h1 style={{
            margin: '0 0 12px 0',
            fontSize: '32px',
            fontWeight: 600
          }}>ü§ñ „Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„Ç∏„Çß„Éç„É¨„Éº„Çø„Éº</h1>
          <p style={{
            margin: 0,
            fontSize: '16px',
            opacity: 0.9
          }}>Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åó„Å¶„ÄÅAI„Åå„Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„ÅÆ„Éâ„É©„Éï„Éà„ÇíËá™ÂãïÁîüÊàê„Åó„Åæ„Åô</p>
        </div>
      </header>

      <main style={{
        flex: 1,
        padding: '40px 0'
      }}>
        <div className="container">
          {state.error && (
            <div className="error-message">
              <span className="error-icon">‚ö†Ô∏è</span>
              <span>{state.error}</span>
              <button onClick={() => setState(prev => ({ ...prev, error: null }))}>
                ‚úï
              </button>
            </div>
          )}

          {state.currentStep === 'upload' && (
            <div className="upload-section">
              <FileUpload
                onFileSelect={handleFileSelect}
                isLoading={state.isLoading}
              />
            </div>
          )}

          {state.currentStep === 'processing' && (
            <div className="processing-section">
              <div className="processing-content">
                <div className="spinner-large"></div>
                <h2>Âá¶ÁêÜ‰∏≠...</h2>
                <p>„Éï„Ç°„Ç§„É´„ÇíËß£Êûê„Åó„Å¶„Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„ÇíÁîüÊàê„Åó„Å¶„ÅÑ„Åæ„Åô</p>
                <div className="processing-steps">
                  <div className="step completed">‚úì „Éï„Ç°„Ç§„É´„Ç¢„ÉÉ„Éó„É≠„Éº„ÉâÂÆå‰∫Ü</div>
                  <div className="step processing">‚è≥ „ÉÜ„Ç≠„Çπ„ÉàÊäΩÂá∫‰∏≠...</div>
                  <div className="step">ü§ñ AIÂàÜÊûê„ÉªÁîüÊàê‰∏≠...</div>
                </div>
              </div>
            </div>
          )}

          {state.currentStep === 'result' && state.uploadedFile && state.extractedText && state.generatedRequirements && (
            <div className="result-section">
              <ResultDisplay
                filename={state.uploadedFile.name}
                extractedText={state.extractedText}
                generatedRequirements={state.generatedRequirements}
                onDownload={handleDownload}
                onReset={handleReset}
              />
            </div>
          )}
        </div>
      </main>

      <footer className="app-footer">
        <div className="container">
          <p>&copy; 2024 „Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„Ç∏„Çß„Éç„É¨„Éº„Çø„Éº</p>
        </div>
      </footer>

    </div>
  );
};

export default App;
