import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import ResultDisplay from './components/ResultDisplay';
import IndividualResultDisplay from './components/IndividualResultDisplay';
import { AppState, GenerationType } from './types';
import { ApiService, downloadAsFile } from './services/api';
import { SystemRequirementsResponse } from './types';

const App: React.FC = () => {
  const [state, setState] = useState<AppState>({
    isLoading: false,
    error: null,
    uploadedFile: null,
    extractedText: null,
    generatedRequirements: null,
    currentStep: 'upload',
    generationType: null,
    individualResults: {},
  });

  const handleFileSelect = async (file: File, generationType: GenerationType = 'comprehensive') => {
    setState(prev => ({
      ...prev,
      isLoading: true,
      error: null,
      uploadedFile: file,
      generationType,
      currentStep: 'processing',
    }));

    try {
      if (generationType === 'comprehensive' || generationType === 'basic') {
        // ÂåÖÊã¨ÁöÑ„ÉªÂü∫Êú¨ÁîüÊàê„ÅÆÂ†¥Âêà
        let response: SystemRequirementsResponse;
        if (generationType === 'comprehensive') {
          response = await ApiService.generateComprehensive(file);
        } else {
          response = await ApiService.uploadAndGenerate(file);
        }

        setState(prev => ({
          ...prev,
          isLoading: false,
          extractedText: response.extracted_text,
          generatedRequirements: response.generated_requirements,
          currentStep: 'result',
        }));
      } else {
        // ÂÄãÂà•ÁîüÊàê„ÅÆÂ†¥Âêà
        let result: string = '';

        switch (generationType) {
          case 'functional-diagram':
            const diagResponse = await ApiService.generateFunctionalDiagram(file);
            result = diagResponse.functional_diagram;
            break;
          case 'external-interfaces':
            const extResponse = await ApiService.generateExternalInterfaces(file);
            result = extResponse.external_interfaces;
            break;
          case 'performance':
            const perfResponse = await ApiService.generatePerformanceRequirements(file);
            result = perfResponse.performance_requirements;
            break;
          case 'security':
            const secResponse = await ApiService.generateSecurityRequirements(file);
            result = secResponse.security_requirements;
            break;
        }

        setState(prev => ({
          ...prev,
          isLoading: false,
          individualResults: {
            ...prev.individualResults,
            [generationType.replace('-', '')]: result
          },
          currentStep: 'result',
        }));
      }
    } catch (error) {
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: error instanceof Error ? error.message : '‰∫àÊúü„Åó„Å™„ÅÑ„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü',
        currentStep: 'upload',
      }));
    }
  };

  const handleDownload = () => {
    if (state.generatedRequirements && state.uploadedFile) {
      const filename = `„Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏_${state.uploadedFile.name.split('.')[0]}.md`;
      downloadAsFile(state.generatedRequirements, filename);
    }
  };

  const handleReset = () => {
    setState({
      isLoading: false,
      error: null,
      uploadedFile: null,
      extractedText: null,
      generatedRequirements: null,
      currentStep: 'upload',
      generationType: null,
      individualResults: {},
    });
  };

  const handleGenerateMore = (file: File) => {
    setState(prev => ({
      ...prev,
      currentStep: 'upload',
      isLoading: false,
      error: null,
    }));
  };

  const getIndividualResult = (generationType: GenerationType, results: any): string => {
    switch (generationType) {
      case 'functional-diagram':
        return results.functionaldiagram || '';
      case 'external-interfaces':
        return results.externalinterfaces || '';
      case 'performance':
        return results.performance || '';
      case 'security':
        return results.security || '';
      default:
        return '';
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column'
    }}>
      <header style={{
        background: 'linear-gradient(135deg, #007bff 0%, #0056b3 100%)',
        color: 'white',
        padding: '32px 0',
        textAlign: 'center'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px'
        }}>
          <h1 style={{
            margin: '0 0 12px 0',
            fontSize: '32px',
            fontWeight: 600
          }}>ü§ñ „Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„Ç∏„Çß„Éç„É¨„Éº„Çø„Éº</h1>
          <p style={{
            margin: 0,
            fontSize: '16px',
            opacity: 0.9
          }}>Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åó„Å¶„ÄÅÊ©üËÉΩÊßãÊàêÂõ≥„ÉªÂ§ñÈÉ®IF„ÉªÊÄßËÉΩ/„Çª„Ç≠„É•„É™„ÉÜ„Ç£Ë¶Å‰ª∂„ÇíÂê´„ÇÄÂåÖÊã¨ÁöÑ„Å™„Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„ÇíËá™ÂãïÁîüÊàê„Åó„Åæ„Åô</p>
        </div>
      </header>

      <main style={{
        flex: 1,
        padding: '40px 0'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px'
        }}>
          {state.error && (
            <div style={{
              backgroundColor: '#f8d7da',
              color: '#721c24',
              padding: '16px',
              borderRadius: '6px',
              marginBottom: '24px',
              display: 'flex',
              alignItems: 'center',
              gap: '12px'
            }}>
              <span>‚ö†Ô∏è</span>
              <span>{state.error}</span>
              <button 
                onClick={() => setState(prev => ({ ...prev, error: null }))}
                style={{
                  background: 'none',
                  border: 'none',
                  color: '#721c24',
                  cursor: 'pointer',
                  fontSize: '16px',
                  marginLeft: 'auto'
                }}
              >
                ‚úï
              </button>
            </div>
          )}

          {state.currentStep === 'upload' && (
            <div className="upload-section">
              <FileUpload
                onFileSelect={handleFileSelect}
                isLoading={state.isLoading}
              />
            </div>
          )}

          {state.currentStep === 'processing' && (
            <div className="processing-section">
              <div className="processing-content">
                <div className="spinner-large"></div>
                <h2>Âá¶ÁêÜ‰∏≠...</h2>
                <p>„Éï„Ç°„Ç§„É´„ÇíËß£Êûê„Åó„Å¶ÂåÖÊã¨ÁöÑ„Å™„Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„ÇíÁîüÊàê„Åó„Å¶„ÅÑ„Åæ„Åô</p>
                <div className="processing-steps">
                  <div className="step completed">‚úì „Éï„Ç°„Ç§„É´„Ç¢„ÉÉ„Éó„É≠„Éº„ÉâÂÆå‰∫Ü</div>
                  <div className="step processing">‚è≥ „ÉÜ„Ç≠„Çπ„ÉàÊäΩÂá∫‰∏≠...</div>
                  <div className="step">ü§ñ AIÂàÜÊûê„ÉªÁîüÊàê‰∏≠ÔºàÊ©üËÉΩÊßãÊàêÂõ≥„ÉªÂ§ñÈÉ®IF„ÉªÊÄßËÉΩ/„Çª„Ç≠„É•„É™„ÉÜ„Ç£Ë¶Å‰ª∂Âê´„ÇÄÔºâ...</div>
                </div>
              </div>
            </div>
          )}

          {state.currentStep === 'result' && state.uploadedFile && (
            <div className="result-section">
              {(state.generationType === 'comprehensive' || state.generationType === 'basic') && 
               state.extractedText && state.generatedRequirements ? (
                <ResultDisplay
                  filename={state.uploadedFile.name}
                  extractedText={state.extractedText}
                  generatedRequirements={state.generatedRequirements}
                  onDownload={handleDownload}
                  onReset={handleReset}
                />
              ) : state.generationType && (
                <IndividualResultDisplay
                  filename={state.uploadedFile.name}
                  generationType={state.generationType}
                  result={getIndividualResult(state.generationType, state.individualResults)}
                  onReset={handleReset}
                  onGenerateMore={handleGenerateMore}
                  uploadedFile={state.uploadedFile}
                />
              )}
            </div>
          )}
        </div>
      </main>

      <footer style={{
        backgroundColor: '#f8f9fa',
        padding: '20px 0',
        textAlign: 'center',
        borderTop: '1px solid #eee'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px'
        }}>
          <p style={{
            margin: 0,
            color: '#666',
            fontSize: '14px'
          }}>&copy; 2024 „Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„Ç∏„Çß„Éç„É¨„Éº„Çø„Éº</p>
        </div>
      </footer>

    </div>
  );
};

export default App;
