import React, { useState } from 'react';
import { GenerationType } from '../types';
import { downloadAsFile } from '../services/api';

interface IndividualResultDisplayProps {
  filename: string;
  generationType: GenerationType;
  result: string;
  onReset: () => void;
  onGenerateMore?: (file: File) => void;
  onGenerateNext?: (generationType: GenerationType) => void;
  uploadedFile?: File | null;
  sessionId?: string | null;
}

const IndividualResultDisplay: React.FC<IndividualResultDisplayProps> = ({
  filename,
  generationType,
  result,
  onReset,
  onGenerateMore,
  onGenerateNext,
  uploadedFile,
  sessionId
}) => {
  const [isExpanded, setIsExpanded] = useState(true);

  const getTypeInfo = (type: GenerationType) => {
    switch (type) {
      case 'comprehensive':
        return { title: 'ÂåÖÊã¨ÁöÑ„Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏', icon: 'üìã', color: '#28a745' };
      case 'basic':
        return { title: 'Âü∫Êú¨„Ç∑„Çπ„ÉÜ„É†Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏', icon: 'üìÑ', color: '#007bff' };
      case 'functional-diagram':
        return { title: 'Ê©üËÉΩÊßãÊàêÂõ≥', icon: 'üîó', color: '#6f42c1' };
      case 'external-interfaces':
        return { title: 'Â§ñÈÉ®„Ç§„É≥„Çø„Éº„Éï„Çß„Éº„ÇπË¶Å‰ª∂', icon: 'üîå', color: '#fd7e14' };
      case 'performance':
        return { title: 'ÊÄßËÉΩË¶Å‰ª∂', icon: '‚ö°', color: '#ffc107' };
      case 'security':
        return { title: '„Çª„Ç≠„É•„É™„ÉÜ„Ç£Ë¶Å‰ª∂', icon: 'üîí', color: '#dc3545' };
      default:
        return { title: 'ÁîüÊàêÁµêÊûú', icon: 'üìÑ', color: '#6c757d' };
    }
  };

  const typeInfo = getTypeInfo(generationType);

  const handleDownload = () => {
    const fileExtension = generationType === 'functional-diagram' ? '.md' : '.md';
    const downloadFilename = `${typeInfo.title}_${filename.split('.')[0]}${fileExtension}`;
    downloadAsFile(result, downloadFilename);
  };

  const handleGenerateMore = () => {
    if (onGenerateMore && uploadedFile) {
      onGenerateMore(uploadedFile);
    }
  };

  const getAvailableGenerationTypes = (): { type: GenerationType; label: string; icon: string }[] => {
    const allTypes = [
      { type: 'functional-diagram' as GenerationType, label: 'Ê©üËÉΩÊßãÊàêÂõ≥', icon: 'üîó' },
      { type: 'external-interfaces' as GenerationType, label: 'Â§ñÈÉ®„Ç§„É≥„Çø„Éº„Éï„Çß„Éº„ÇπË¶Å‰ª∂', icon: 'üîå' },
      { type: 'performance' as GenerationType, label: 'ÊÄßËÉΩË¶Å‰ª∂', icon: '‚ö°' },
      { type: 'security' as GenerationType, label: '„Çª„Ç≠„É•„É™„ÉÜ„Ç£Ë¶Å‰ª∂', icon: 'üîí' }
    ];

    // ÁèæÂú®„ÅÆ„Çø„Ç§„Éó‰ª•Â§ñ„ÇíËøî„Åô
    return allTypes.filter(item => item.type !== generationType);
  };

  const handleGenerateNext = (nextType: GenerationType) => {
    if (onGenerateNext) {
      onGenerateNext(nextType);
    }
  };

  return (
    <div style={{
      width: '100%',
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px 0'
    }}>
      {/* „Éò„ÉÉ„ÉÄ„Éº */}
      <div style={{
        backgroundColor: '#fff',
        border: '1px solid #e9ecef',
        borderRadius: '12px',
        padding: '24px',
        marginBottom: '24px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
      }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          marginBottom: '16px'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '12px'
          }}>
            <span style={{ 
              fontSize: '24px',
              backgroundColor: typeInfo.color,
              color: 'white',
              padding: '8px',
              borderRadius: '8px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              {typeInfo.icon}
            </span>
            <div>
              <h2 style={{
                margin: 0,
                fontSize: '20px',
                color: '#333',
                fontWeight: 600
              }}>{typeInfo.title}</h2>
              <p style={{
                margin: '4px 0 0 0',
                fontSize: '14px',
                color: '#666'
              }}>„Éï„Ç°„Ç§„É´: {filename}</p>
            </div>
          </div>

          <div style={{
            display: 'flex',
            gap: '12px'
          }}>
            <button
              onClick={handleDownload}
              style={{
                padding: '8px 16px',
                border: '1px solid #007bff',
                borderRadius: '6px',
                backgroundColor: '#fff',
                color: '#007bff',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: 500,
                display: 'flex',
                alignItems: 'center',
                gap: '6px',
                transition: 'all 0.3s ease'
              }}
            >
              üì• „ÉÄ„Ç¶„É≥„É≠„Éº„Éâ
            </button>

            {onGenerateMore && (sessionId || uploadedFile) && (
              <button
                onClick={handleGenerateMore}
                style={{
                  padding: '8px 16px',
                  border: '1px solid #28a745',
                  borderRadius: '6px',
                  backgroundColor: '#fff',
                  color: '#28a745',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: 500,
                  display: 'flex',
                  alignItems: 'center',
                  gap: '6px',
                  transition: 'all 0.3s ease'
                }}
              >
                ‚ûï ‰ªñ„ÅÆË¶Å‰ª∂„ÇÇÁîüÊàê
              </button>
            )}

            <button
              onClick={onReset}
              style={{
                padding: '8px 16px',
                border: '1px solid #6c757d',
                borderRadius: '6px',
                backgroundColor: '#fff',
                color: '#6c757d',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: 500,
                display: 'flex',
                alignItems: 'center',
                gap: '6px',
                transition: 'all 0.3s ease'
              }}
            >
              üîÑ ÊúÄÂàù„Åã„Çâ
            </button>
          </div>
        </div>

        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '16px',
          padding: '12px 16px',
          backgroundColor: '#f8f9fa',
          borderRadius: '6px'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '6px'
          }}>
            <span style={{ fontSize: '14px', color: '#666' }}>ÁîüÊàêÂÆå‰∫Ü</span>
            <span style={{ fontSize: '16px', color: '#28a745' }}>‚úÖ</span>
          </div>
          <div style={{
            height: '16px',
            width: '1px',
            backgroundColor: '#dee2e6'
          }}></div>
          <span style={{ fontSize: '14px', color: '#666' }}>
            ÊñáÂ≠óÊï∞: {result.length.toLocaleString()}ÊñáÂ≠ó
          </span>
        </div>
      </div>

      {/* ÁµêÊûúË°®Á§∫ */}
      <div style={{
        backgroundColor: '#fff',
        border: '1px solid #e9ecef',
        borderRadius: '12px',
        overflow: 'hidden',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
      }}>
        <div 
          onClick={() => setIsExpanded(!isExpanded)}
          style={{
            padding: '16px 24px',
            backgroundColor: '#f8f9fa',
            borderBottom: '1px solid #e9ecef',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            transition: 'background-color 0.3s ease'
          }}
        >
          <h3 style={{
            margin: 0,
            fontSize: '16px',
            color: '#333',
            fontWeight: 600
          }}>ÁîüÊàê„Åï„Çå„Åü{typeInfo.title}</h3>
          <span style={{
            fontSize: '18px',
            color: '#666',
            transform: isExpanded ? 'rotate(180deg)' : 'none',
            transition: 'transform 0.3s ease'
          }}>
            ‚ñº
          </span>
        </div>

        {isExpanded && (
          <div style={{
            padding: '24px',
            maxHeight: '70vh',
            overflowY: 'auto'
          }}>
            <div style={{
              backgroundColor: '#f8f9fa',
              border: '1px solid #e9ecef',
              borderRadius: '6px',
              padding: '20px',
              fontFamily: 'Monaco, Consolas, "Courier New", monospace',
              fontSize: '13px',
              lineHeight: '1.6',
              color: '#333',
              whiteSpace: 'pre-wrap',
              wordBreak: 'break-word'
            }}>
              {result}
            </div>
          </div>
        )}
      </div>

      {/* Ê¨°„ÅÆÁîüÊàêÈÅ∏Êäû„Çª„ÇØ„Ç∑„Éß„É≥ */}
      {sessionId && onGenerateNext && (
        <div style={{
          backgroundColor: '#fff',
          border: '1px solid #e9ecef',
          borderRadius: '12px',
          padding: '24px',
          marginTop: '24px',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
        }}>
          <h3 style={{
            margin: '0 0 16px 0',
            fontSize: '18px',
            color: '#333',
            fontWeight: 600,
            textAlign: 'center'
          }}>üîÑ Âêå„Åò„Éâ„Ç≠„É•„É°„É≥„Éà„Åã„Çâ‰ªñ„ÅÆË¶Å‰ª∂„ÇÇÁîüÊàê</h3>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
            gap: '12px',
            marginTop: '16px'
          }}>
            {getAvailableGenerationTypes().map((item) => (
              <button
                key={item.type}
                onClick={() => handleGenerateNext(item.type)}
                style={{
                  padding: '12px 16px',
                  border: '2px solid #e9ecef',
                  borderRadius: '8px',
                  backgroundColor: '#fff',
                  color: '#333',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: 500,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '8px',
                  transition: 'all 0.3s ease',
                  ':hover': {
                    borderColor: '#007bff',
                    backgroundColor: '#f8f9fa'
                  }
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.borderColor = '#007bff';
                  e.currentTarget.style.backgroundColor = '#f8f9fa';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.borderColor = '#e9ecef';
                  e.currentTarget.style.backgroundColor = '#fff';
                }}
              >
                <span style={{ fontSize: '16px' }}>{item.icon}</span>
                <span>{item.label}</span>
              </button>
            ))}
          </div>

          <p style={{
            margin: '16px 0 0 0',
            fontSize: '12px',
            color: '#666',
            textAlign: 'center'
          }}>
            üí° Êó¢„Å´„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åï„Çå„Åü„Éâ„Ç≠„É•„É°„É≥„Éà„Çí‰ΩøÁî®„Åô„Çã„Åü„ÇÅ„ÄÅÂÜç„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„ÅØ‰∏çË¶Å„Åß„Åô
          </p>
        </div>
      )}

      {/* „Éï„ÉÉ„Çø„ÉºÊÉÖÂ†± */}
      <div style={{
        textAlign: 'center',
        marginTop: '24px',
        padding: '16px',
        backgroundColor: '#f8f9fa',
        borderRadius: '8px',
        border: '1px solid #e9ecef'
      }}>
        <p style={{
          margin: 0,
          fontSize: '14px',
          color: '#666'
        }}>
          üí° „Éí„É≥„Éà: MermaidË®òÊ≥ï„ÅÆÂõ≥Ë°®„ÅØ„ÄÅMermaidÂØæÂøú„Ç®„Éá„Ç£„Çø„ÇÑGitHub„ÅßË°®Á§∫„Åß„Åç„Åæ„Åô
        </p>
      </div>
    </div>
  );
};

export default IndividualResultDisplay;
