import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { FileUploadProps, GenerationType } from '../types';
import { validateFile } from '../services/api';
import GenerationTypeSelector from './GenerationTypeSelector';

const FileUpload: React.FC<FileUploadProps> = ({ onFileSelect, isLoading, sessionId }) => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [selectedType, setSelectedType] = useState<GenerationType>('comprehensive');
  const [showTypeSelector, setShowTypeSelector] = useState(false);

  const onDrop = useCallback((acceptedFiles: File[]) => {
    if (acceptedFiles.length > 0) {
      const file = acceptedFiles[0];
      const error = validateFile(file);

      if (error) {
        alert(error);
        return;
      }

      setSelectedFile(file);
      setShowTypeSelector(true);
    }
  }, []);

  // „Çª„ÉÉ„Ç∑„Éß„É≥„Åå„ÅÇ„ÇãÂ†¥Âêà„ÄÅ„Éï„Ç°„Ç§„É´„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Å™„Åó„ÅßÁõ¥Êé•ÁîüÊàêÈÅ∏ÊäûÁîªÈù¢„Å∏
  const handleSessionBasedGeneration = () => {
    if (sessionId) {
      setShowTypeSelector(true);
    }
  };

  const handleFileSelectClick = () => {
    // „Çª„ÉÉ„Ç∑„Éß„É≥„Åå„ÅÇ„ÇãÂ†¥Âêà„ÅØ„Éï„Ç°„Ç§„É´„Å™„Åó„ÅßÁîüÊàêÔºàÂÄãÂà•ÁîüÊàê„ÅÆ„ÅøÔºâ
    if (sessionId && selectedType !== 'comprehensive' && selectedType !== 'basic') {
      onFileSelect(new File([], ''), selectedType);
      setSelectedFile(null);
      setShowTypeSelector(false);
    } else if (selectedFile || sessionId) {
      onFileSelect(selectedFile || new File([], ''), selectedType);
      setSelectedFile(null);
      setShowTypeSelector(false);
    }
  };

  const handleCancel = () => {
    setSelectedFile(null);
    setShowTypeSelector(false);
  };

  const handleTypeSelect = (type: GenerationType) => {
    setSelectedType(type);
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'application/msword': ['.doc'],
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': ['.xlsx'],
      'application/vnd.ms-excel': ['.xls'],
    },
    multiple: false,
    disabled: isLoading,
  });

  if (showTypeSelector && selectedFile) {
    return (
      <div style={{
        width: '100%',
        maxWidth: '900px',
        margin: '0 auto'
      }}>
        <div style={{
          backgroundColor: '#f8f9fa',
          border: '1px solid #e9ecef',
          borderRadius: '8px',
          padding: '20px',
          marginBottom: '20px',
          textAlign: 'center'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '12px',
            marginBottom: '12px'
          }}>
            <span style={{ fontSize: '20px' }}>üìÑ</span>
            <span style={{ fontWeight: 600, color: '#333' }}>ÈÅ∏Êäû„Åï„Çå„Åü„Éï„Ç°„Ç§„É´:</span>
            <span style={{ color: '#007bff' }}>{selectedFile.name}</span>
          </div>
          <p style={{
            margin: 0,
            fontSize: '14px',
            color: '#666'
          }}>„Éï„Ç°„Ç§„É´„Çµ„Ç§„Ç∫: {(selectedFile.size / 1024 / 1024).toFixed(2)} MB</p>
        </div>

        <GenerationTypeSelector
          onTypeSelect={handleTypeSelect}
          isLoading={isLoading}
        />

        <div style={{
          display: 'flex',
          gap: '16px',
          justifyContent: 'center',
          marginTop: '30px'
        }}>
          <button
            onClick={handleCancel}
            disabled={isLoading}
            style={{
              padding: '12px 24px',
              border: '2px solid #ccc',
              borderRadius: '6px',
              backgroundColor: '#fff',
              color: '#333',
              cursor: isLoading ? 'not-allowed' : 'pointer',
              fontSize: '16px',
              fontWeight: 500,
              opacity: isLoading ? 0.6 : 1,
              transition: 'all 0.3s ease'
            }}
          >
            „Ç≠„É£„É≥„Çª„É´
          </button>
          <button
            onClick={handleFileSelectClick}
            disabled={isLoading}
            style={{
              padding: '12px 32px',
              border: 'none',
              borderRadius: '6px',
              backgroundColor: '#007bff',
              color: 'white',
              cursor: isLoading ? 'not-allowed' : 'pointer',
              fontSize: '16px',
              fontWeight: 600,
              opacity: isLoading ? 0.6 : 1,
              transition: 'all 0.3s ease',
              boxShadow: '0 2px 4px rgba(0,123,255,0.2)'
            }}
          >
            {isLoading ? 'Âá¶ÁêÜ‰∏≠...' : 'ÁîüÊàêÈñãÂßã'}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={{
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto'
    }}>
      {sessionId && (
        <div style={{
          backgroundColor: '#e7f3ff',
          border: '1px solid #b3d9ff',
          borderRadius: '8px',
          padding: '16px',
          marginBottom: '20px',
          textAlign: 'center'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '8px',
            marginBottom: '8px'
          }}>
            <span style={{ fontSize: '20px' }}>üíæ</span>
            <span style={{ fontWeight: 600, color: '#0066cc' }}>„Ç¢„ÉÉ„Éó„É≠„Éº„ÉâÊ∏à„Åø„Éâ„Ç≠„É•„É°„É≥„Éà</span>
          </div>
          <p style={{
            margin: '0 0 12px 0',
            fontSize: '14px',
            color: '#0066cc'
          }}>
            Êó¢„Å´„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åï„Çå„Åü„Éâ„Ç≠„É•„É°„É≥„Éà„Åã„ÇâËøΩÂä†„ÅÆË¶Å‰ª∂„ÇíÁîüÊàê„Åß„Åç„Åæ„Åô
          </p>
          <button
            onClick={handleSessionBasedGeneration}
            style={{
              padding: '8px 20px',
              border: 'none',
              borderRadius: '6px',
              backgroundColor: '#0066cc',
              color: 'white',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: 500
            }}
          >
            ËøΩÂä†Ë¶Å‰ª∂„ÇíÁîüÊàê
          </button>
        </div>
      )}

      <div
        {...getRootProps()}
        style={{
          border: '2px dashed #ccc',
          borderRadius: '8px',
          padding: '40px',
          textAlign: 'center',
          cursor: isLoading ? 'not-allowed' : 'pointer',
          transition: 'all 0.3s ease',
          backgroundColor: isDragActive ? '#e6f3ff' : '#fafafa',
          borderColor: isDragActive ? '#007bff' : '#ccc',
          opacity: isLoading ? 0.6 : 1
        }}
      >
        <input {...getInputProps()} />
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: '16px'
        }}>
          <div style={{
            fontSize: '48px',
            opacity: 0.7
          }}>üìÑ</div>
          {isLoading ? (
            <div style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: '12px'
            }}>
              <div style={{
                width: '32px',
                height: '32px',
                border: '3px solid #f3f3f3',
                borderTop: '3px solid #007bff',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite'
              }}></div>
              <p style={{ margin: 0, fontSize: '16px', color: '#333' }}>Âá¶ÁêÜ‰∏≠...</p>
            </div>
          ) : (
            <>
              {isDragActive ? (
                <p style={{ margin: 0, fontSize: '16px', color: '#333' }}>„Éï„Ç°„Ç§„É´„Çí„Åì„Åì„Å´„Éâ„É≠„ÉÉ„Éó„Åó„Å¶„Åè„Å†„Åï„ÅÑ</p>
              ) : (
                <div>
                  <p style={{ margin: 0, fontSize: '16px', color: '#333' }}>
                    {sessionId ? 'Êñ∞„Åó„ÅÑË¶Å‰ª∂ÂÆöÁæ©Êõ∏„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ' : 'Ë¶Å‰ª∂ÂÆöÁæ©Êõ∏„Çí„Éâ„É©„ÉÉ„Ç∞&„Éâ„É≠„ÉÉ„Éó„Åô„Çã„Åã„ÄÅ„ÇØ„É™„ÉÉ„ÇØ„Åó„Å¶„Éï„Ç°„Ç§„É´„ÇíÈÅ∏Êäû'}
                  </p>
                  <p style={{ fontSize: '14px', color: '#666', marginTop: '8px' }}>ÂØæÂøúÂΩ¢Âºè: PDF, Word (.docx, .doc), Excel (.xlsx, .xls)</p>
                  <p style={{ fontSize: '12px', color: '#999', marginTop: '4px' }}>ÊúÄÂ§ß„Éï„Ç°„Ç§„É´„Çµ„Ç§„Ç∫: 10MB</p>
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default FileUpload;
